INSERT INTO `ads` (`id`, `name`, `content`, `ad_region_id`, `label`, `sort_order`, `created_user_id`, `created_timestamp`, `last_modified_user_id`, `last_modified_timestamp`, `caption`) VALUES
(18, 'Experience & Expertise', '<div class="theme-sample-ad-6">&nbsp;</div>', 1, '6', 6, 2, 1333955843, 2, 1364642286, '<h1>Experience &amp; Expertise</h1>\r\n<h2>Professional Web Architects ready to jump in.</h2>\r\n<p>From installation to customization and support, our team of professionals with over 20 years of experience<br /> can help you build the website you need. No project is too large or too small!</p>'),
(14, 'Front End Design', '<div class="theme-sample-ad-5">&nbsp;</div>', 1, '5', 5, 2, 1333079995, 2, 1365633091, '<h1>Built-In Theme Designer</h1>\r\n<h2>Create your own web designs.</h2>\r\n<p>With point-and-click design options, you can create a professional-looking site that works on all browsers and mobile devices.<br />Pick a theme and add custom fonts, colors, and backgrounds &mdash; all without any CSS coding or additional web design tools.</p>'),
(30, 'Experience & Expertise Mobile', '<div class="theme-sample-ad-6-mobile">&nbsp;</div>', 10, '6', 6, 2, 1361912609, 2, 1361914255, '<h1>Experience &amp; Expertise</h1>\r\n<p>Professional Web Architects ready to jump in.</p>'),
(23, 'All-inclusive Functionality', '<div class="theme-sample-ad-1">&nbsp;</div>', 1, '1', 1, 2, 1360953392, 2, 1365632789, '<h1>All-In-One Functionality</h1>\r\n<h2>One software tool does it all.</h2>\r\n<p>To design, build, and manage a professional website, you no longer need several complex and expensive software programs.<br />Now you can do it all with one online tool.</p>'),
(26, 'Web Development Without Coding', '<div class="theme-sample-ad-2">&nbsp;</div>', 1, '2', 2, 2, 1361003224, 2, 1361527628, '<h1>Web Development For All</h1>\r\n<h2>Create your own secure, database-driven website without coding.</h2>\r\n<p>You don''t need to hire expensive programmers to code the site of your dreams.<br />Now you can build it yourself using our point-and-click software to suit your needs.</p>'),
(27, 'Mobile Ready', '<div class="theme-sample-ad-3">&nbsp;</div>', 1, '3', 3, 2, 1361003240, 2, 1361527647, '<h1>Mobile Ready</h1>\r\n<h2>One Site.&nbsp; Many Devices.</h2>\r\n<p>While most website software platforms require you to create and manage different web pages for your mobile visitors &mdash; we don''t.<br />While responsive design is good, total freedom on the layout and presentation of your pages on mobile devices is better.</p>'),
(25, 'Built-In Modules', '<div class="theme-sample-ad-4">&nbsp;</div>', 1, '4', 4, 2, 1361000021, 2, 1365631964, '<h1>Built-In Modules</h1>\r\n<h2>Say no to plug-ins.</h2>\r\n<p>No need to worry about out-of-date, poorly supported and many times insecure plug-ins that may not work together.<br />All modules are included and with continuous software updates, your site will always have the latest features.</p>'),
(31, 'Front End Design Mobile', '<div class="theme-sample-ad-5-mobile">&nbsp;</div>', 10, '5', 5, 2, 1361912650, 2, 1361914371, '<h1>Built-In Theme Designer</h1>\r\n<p>Create your own web designs.</p>'),
(32, 'Built-In Modules Mobile', '<div class="theme-sample-ad-4-mobile">&nbsp;</div>', 10, '4', 4, 2, 1361962435, 2, 1361962710, '<h1>Built-In Modules</h1>\r\n<p>Say no to plug-ins.</p>'),
(33, 'Mobile Ready Mobile', '<div class="theme-sample-ad-3-mobile">&nbsp;</div>', 10, '3', 3, 2, 1361962487, 2, 1361962696, '<h1>Mobile Ready</h1>\r\n<p>One Site.&nbsp; Many Devices.</p>'),
(34, 'Web Development Without Coding Mobile', '<div class="theme-sample-ad-2-mobile">&nbsp;</div>', 10, '2', 2, 2, 1361962542, 2, 1361964219, '<h1>Web Development For All</h1>\r\n<p>Create your own secure, database-driven website without coding.</p>'),
(35, 'All-inclusive Functionality Mobile', '<div class="theme-sample-ad-1-mobile">&nbsp;</div>', 10, '1', 1, 2, 1361962591, 2, 1364572935, '<h1>All-In-One Functionality</h1>\r\n<p>One software tool does it all.</p>'),
(36, 'testimonial-1', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><span class="text-quote">Before I got married I had six theories about bringing up children; <br />now I have six children and no theories.<br />&mdash;John Wilmot</span></p>\r\n<p>&nbsp;</p>', 11, '', 0, 2, 1364810573, 2, 1364811128, ''),
(37, 'testimonial-2', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><span class="text-quote">What the world needs is more geniuses with humility, there are so few of us left.<br />&mdash;Oscar Levant</span></p>\r\n<p>&nbsp;</p>', 11, '', 0, 2, 1364810615, 2, 1364810964, ''),
(38, 'testimonial-3', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><span class="text-quote">Always forgive your enemies; nothing annoys them so much.<br />&mdash;Oscar Wilde</span></p>\r\n<p>&nbsp;</p>', 11, '', 0, 2, 1364810638, 2, 1364810941, ''),
(39, 'testimonial-4', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><span class="text-quote">Those who believe in telekinetics, raise my hand.<br />&mdash;Kurt Vonnegut</span></p>\r\n<p>&nbsp;</p>', 11, '', 0, 2, 1364810655, 2, 1364810915, ''),
(40, 'testimonial-5', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><span class="text-quote">I quit therapy because my analyst was trying to help me behind my back.<br />&mdash;Richard Lewis</span></p>\r\n<p>&nbsp;</p>', 11, '', 0, 2, 1364810670, 2, 1364810890, ''),
(41, 'testimonial-6', '<p>&nbsp;</p>\r\n<p style="text-align: center;"><span class="text-quote">If the lessons of history teach us anything<br /> it is that nobody learns the lessons history teaches us.<br />&mdash;Anon</span></p>\r\n<p>&nbsp;</p>', 11, '', 0, 2, 1364810684, 2, 1364811182, '');
